import React, { Component } from 'react'

export class NewsPage extends Component {
  state = {
    data: [],
    totalRecords:0,
    limit: 10
}
componentDidMount(){
  this.loadData(1);
}
loadData = async(page) =>{
  fetch.get(`https://reqres.in/api/users?page=`+page)
      .then(res => {
          const data = res.data;
          this.setState({
              data: data.data,
              totalRecords : data.total ? data.total : 0,
              limit : data.per_page ? data.per_page : 6
          })
      });
}
  render() {
    return (
      <div>NewsPage</div>
    )
  }
}

export default NewsPage
