import React, { Component } from 'react'
import NewsAPI from '../../API/NewsPageAPI'

export class NewsPage extends Component {
  state = {
    data: [],
    totalRecords:0,
    limit: 10
}
componentDidMount(){
  this.loadData(1);
}
loadData = async(page) =>{
  fetch(
    NewsAPI.getNewsPageAPI(1)
    .then(res => {
        const data = res.data;
        this.setState({
            data: data.data,
            totalRecords : data.totalItem ? data.totalItem : 0,
            limit : data.size ? data.size : 6
        })
    })
  )
}
  render() {
    const { data, totalRecords, limit } = this.state;
    return (
      <>
        <div className="flex column-center w-3/5 pt-10 pb-10 mb-10 shadow-lg">
          <div className="bg-yellow-500 w-2/3">
            <div className="bg-blue-50 h-11">
            {
              data && data.length > 0 ?
              data.map((item,index)=>(
                  <a href="">
                    {item.content}
                  </a>
              )) :
              <h4>No Data Found!!</h4>
              }
            </div>
          </div>
        </div>      
      </>    
    )
  }
}

export default NewsPage
