import React,{useEffect, useState} from 'react'
import axios from 'axios'
import NewsAPI from '../../API/NewsPageAPI'
import Pagination from "react-js-pagination";

function News() {
  const [DataNotice,setDataNotice] = useState([]);
  const getData = async(page)=>{
    const result = await NewsAPI.getNewsPageAPI(page); 
    setDataNotice(result.data);
    console.log(DataNotice);

  };
  useEffect(() => { 
    getData(2);
  },[])

  return (
    // <div className="border-t-8 border-teal-500 bg-teal-400 p-8 inline-block">
    <div>
      <div className="flex column-center w-3/5 pt-10 pb-10 mb-10 shadow-lg">
        {/* <div className="w-1/3 bg-teal-500 ">
           
        </div>
        <div className="bg-yellow-500 w-2/3">
          <div className="bg-blue-50 h-11">
            <p>{DataNotice?.length > 0 && <h1> xin chao</h1>}</p>
          </div>
          <div>
            {DataNotice.shortDescription}
          </div>
        </div> */}
        {DataNotice.length > 0 && DataNotice.data?.map((item,index) => (<option key={item?.id} value={item?.code}></option>))}
      </div>


      <div className="paginationBox">
        <Pagination
          activePage={1}
          itemsCountPerPage={Number(10)}
          totalItemsCount={0}
          onChange={0}
          nextPageText="Next"
          prevPageText="Prev"
          firstPageText="1st"
          lastPageText="Last"
          itemClass="page-item"
          linkClass="page-link"
          activeClass="pageItemActive"
          activeLinkClass="pageLinkActive"
        />
      </div>
      
    </div>
    
  )
}

export default News
