import React,{Component, useEffect, useState, useRef } from 'react'
import axios from 'axios'
import NewsAPI from '../../API/NewsAPI'
import Loader from '../loader/Loader'
import Pagination from "react-js-pagination";
import { useParams } from 'react-router-dom';

function News() {
  const state = {
    data: []
}
  const {id} =useParams();
  const [DataNotice,setDataNotice] = useState([]);
  const getData = async(id)=>{
    const result = await fetch(NewsAPI.getNewsAPI(id));
    this.setState({data: result.data})
    setDataNotice(result.data);

  };
  useEffect(() => { 
    getData(id);
    console.log(id);
    console.log(DataNotice);

  },[])

  return (
    // <div className="border-t-8 border-teal-500 bg-teal-400 p-8 inline-block">
    <div>
      {
      DataNotice && DataNotice.length > 0 ?
      DataNotice.map((item,index)=>(
          <a href="">
            {item.first_name}
          </a>
      )) :
      <Loader/>
      }    
    </div>
  )
}

export default News
